import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

const DataTableComponent = ({ formData, setFormData }) => {
  const [editingRows, setEditingRows] = useState({});
  const [newData, setNewData] = useState({
    id: null,
    Name: '',
    mailId: '',
    Profile: '',
    Country: '',
  });

  useEffect(() => {
    setFormData(formData || []);
  }, [formData, setFormData]);

  const editRow = (rowData) => {
    setEditingRows((prevState) => ({ ...prevState, [rowData.id]: true }));
  };

  const cancelEditRow = (rowData) => {
    setEditingRows((prevState) => ({ ...prevState, [rowData.id]: false }));
  };

  const saveEditRow = (rowData) => {
    setEditingRows((prevState) => ({ ...prevState, [rowData.id]: false }));
    // You can add logic here to save the edited data, e.g., send it to the backend.
  };

  const deleteRow = (rowData) => {
    // You can add logic here to delete the row, e.g., send a delete request to the backend.
    // In this example, we'll remove the row from the local state.
    setFormData((prevData) => prevData.filter((item) => item.id !== rowData.id));
  };

  const addRow = () => {
    if (!newData.Name || !newData.mailId || !newData.Profile || !newData.Country) {
      return;
    }

    // Generate a unique ID for the new row (this can be done differently depending on your requirements).
    const newId = formData.length + 1;
    setFormData((prevData) => [...prevData, { ...newData, id: newId }]);
    setEditingRows((prevState) => ({ ...prevState, [newId]: true })); // Enable edit mode for the newly added row
    setNewData({ // Clear the new data for the next entry
      id: null,
      Name: '',
      mailId: '',
      Profile: '',
      Country: '',
    });
  };

  const actionBodyTemplate = (rowData) => {
    if (editingRows[rowData.id]) {
      return (
        <div>
          <button className="p-button p-button-success p-mr-2" onClick={() => saveEditRow(rowData)}>
            Save
          </button>
          <button className="p-button p-button-danger" onClick={() => cancelEditRow(rowData)}>
            Cancel
          </button>
        </div>
      );
    } else {
      return (
        <button className="p-button p-button-primary" onClick={() => editRow(rowData)}>
          Edit
        </button>
      );
    }
  };

  const onEditorValueChange = (props, value) => {
    setNewData((prevData) => ({ ...prevData, [props.field]: value }));
  };

  const columns = [
    { field: 'id', header: 'ID' },
    { field: 'Name', header: 'Name' },
    { field: 'mailId', header: 'Mail Id' },
    { field: 'Profile', header: 'Profile' },
    { field: 'Country', header: 'Country' },
    { field: 'action', header: 'Action', body: actionBodyTemplate },
  ];

  return (
    <div>
      <h3>DataTable</h3>
      <div>
        <input
          type="text"
          value={newData.Name}
          onChange={(e) => setNewData({ ...newData, Name: e.target.value })}
          placeholder="Name"
        />
        <input
          type="text"
          value={newData.mailId}
          onChange={(e) => setNewData({ ...newData, mailId: e.target.value })}
          placeholder="Mail Id"
        />
        <input
          type="text"
          value={newData.Profile}
          onChange={(e) => setNewData({ ...newData, Profile: e.target.value })}
          placeholder="Profile"
        />
        <input
          type="text"
          value={newData.Country}
          onChange={(e) => setNewData({ ...newData, Country: e.target.value })}
          placeholder="Country"
        />
        <button className="p-button p-button-success p-ml-2" onClick={addRow}>
          Add
        </button>
      </div>
      <DataTable value={formData}>
        {columns.map((column) => (
          <Column key={column.field} field={column.field} header={column.header} body={column.body} />
        ))}
      </DataTable>
    </div>
  );
};

export default DataTableComponent;
