import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const CombinedBarChart = ({ datasets }) => {
  const labels = datasets[0].data.map((entry, index) => index);

  const options = {
    indexAxis: 'y',
    plugins: {
      title: {
        display: true,
        text: 'Total Invocations',
        align: 'start',
        padding: {
          top: 10,
          left: 20,
        },
      },
      legend: {
        position: 'right',
      },
    },
    scales: {
      x: {
        stacked: true,
        beginAtZero: true,
        title: {
          display: true,
          text: 'Remediation',
        },
      },
      y: {
        stacked: true,
        ticks: {
          autoSkip: false,
          stepSize: 1, // Display dates as units on y-axis
        },
      },
    },
  };

  return (
    <div className="combined-bar-chart">
      <Bar data={{ labels, datasets }} options={options} />
    </div>
  );
};

export default CombinedBarChart;
