import React from 'react';
import { Bar } from 'react-chartjs-2';
import { saveAs } from 'file-saver';
import './CombinedBarChart.css';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const CombinedBarChart = ({ datasets }) => {
  const labels = datasets.length > 0 ? datasets[0].data.map(entry => entry.date) : [];

  const chartData = {
    labels,
    datasets: datasets.map((dataset, index) => ({
      label: `Data Table ${index + 1}`,
      backgroundColor: getBackgroundColor(index),
      data: dataset.data.map(entry => entry.totalInvocations),
    })),
  };

  const options = {
    scales: {
      x: {
        stacked: true,
      },
      y: {
        stacked: true,
      },
    },
    plugins: {
      legend: {
        position: 'top',
        labels: {
          usePointStyle: true,
        },
      },
    },
  };

  const chartStyle = {
    width: '80%', // Adjust the width as needed
    margin: '0 auto',
  };

  const handleDownload = async format => {
    if (format === 'CSV') {
      // Handle CSV export
    } else {
      const chart = document.querySelector('.chartjs-render-monitor');
      if (!chart) {
        return;
      }

      const canvas = chart.querySelector('canvas');
      if (!canvas) {
        return;
      }

      canvas.toBlob(blob => {
        saveAs(blob, `chart.${format.toLowerCase()}`);
      });
    }
  };

  return (
    <div className="combined-bar-chart" style={chartStyle}>
      <div className="chart-menu">
        <div className="menu-icon" onClick={() => handleDownload('SVG')}>
          <i className="fas fa-download"></i>
        </div>
        <div className="menu-icon" onClick={() => handleDownload('PNG')}>
          <i className="fas fa-download"></i>
        </div>
        <div className="menu-icon" onClick={() => handleDownload('CSV')}>
          <i className="fas fa-download"></i>
        </div>
      </div>
      <Bar data={chartData} options={options} />
    </div>
  );
};

const getBackgroundColor = index => {
  const colors = ['#FF5733', '#33FF57', '#3357FF']; // Example colors
  return colors[index % colors.length];
};

export default CombinedBarChart;
