import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import './CombinedBarChart.css'; // Import your CSS file
import { saveAs } from 'file-saver'; // Import the file-saver library
Chart.register(...registerables);

const CombinedBarChart = ({ datasets }) => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const downloadChart = (format) => {
    // Convert the chart to the specified format (SVG or PNG)
    const canvas = document.querySelector('.combined-bar-chart canvas');
    if (!canvas) return;

    canvas.toBlob(function (blob) {
      saveAs(blob, `chart.${format}`);
    }, `image/${format}`);
  };

  const dates = datasets[0].data.map(entry => entry.date);
  const chartData = {
    labels: dates,
    datasets: datasets.map(dataset => ({
      label: dataset.label,
      backgroundColor: dataset.backgroundColor,
      data: dataset.data.map(entry => entry.totalInvocations),
    })),
  };

  const options = {
    indexAxis: 'y',
    plugins: {
      legend: {
        position: 'right',
      },
    },
    scales: {
      y: {
        stacked: true,
        ticks: {
          autoSkip: false,
        },
      },
      x: {
        stacked: true,
        beginAtZero: true,
        title: {
          display: true,
          text: 'REMEDIATION',
        },
      },
    },
  };

  return (
    <div className="combined-bar-chart">
      <div className="toggle-dropdown">
        <button onClick={toggleDropdown}>Toggle Menu</button>
        {isDropdownOpen && (
          <div className="dropdown-content">
            <button onClick={() => downloadChart('svg')}>Download SVG</button>
            <button onClick={() => downloadChart('png')}>Download PNG</button>
            <button onClick={() => downloadChart('csv')}>Download CSV</button>
          </div>
        )}
      </div>
      <Bar data={chartData} options={options} />
    </div>
  );
};

export default CombinedBarChart;
