import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const CombinedBarChart = ({ datasets }) => {
  const labels = datasets.length > 0 ? datasets[0].data.map(entry => entry.date) : [];

  const chartData = {
    labels: labels,
    datasets: datasets.map((dataset, index) => ({
      label: `Data ${index + 1}`,
      backgroundColor: dataset.color,
      data: labels.map(label =>
        dataset.data.find(entry => entry.date === label)?.totalInvocations || 0
      ),
    })),
  };

  const options = {
    indexAxis: 'y',
    scales: {
      x: {
        stacked: true,
        title: {
          display: true,
          text: 'Remediation',
        },
      },
      y: {
        stacked: true,
      },
    },
    plugins: {
      title: {
        display: true,
        text: 'Total Invocations',
        position: 'top',
      },
      legend: {
        display: true,
        position: 'right', // Adjust the position as needed
      },
    },
    custom: {
      legendSquare: [
        {
          draw: function (context, legendItem, { x, y, width, height, ctx }) {
            const centerX = x + width / 2;
            const topY = y + 10;

            ctx.fillStyle = legendItem.fillStyle;
            ctx.fillRect(centerX - 6, topY, 12, 12); // Adjust size as needed
          },
        },
      ],
    },
  };

  const chartStyle = {
    width: '80%', // Adjust the width as needed
    margin: '0 auto',
  };

  const [menuVisible, setMenuVisible] = useState(false);

  const toggleMenu = () => {
    setMenuVisible(!menuVisible);
  };

  const handleDownload = format => {
    // Handle the download based on the chosen format
    console.log(`Downloading ${format}`);
  };

  return (
    <div className="combined-bar-chart" style={chartStyle}>
      <div className={`chart-menu ${menuVisible ? 'open' : ''}`}>
        <div className="menu-icon" onClick={toggleMenu}>
          <i className={`fas fa-bars ${menuVisible ? 'open' : ''}`} />
        </div>
        {menuVisible && (
          <ul className="menu-options">
            <li>
              <button onClick={() => handleDownload('SVG')}>Download SVG</button>
            </li>
            <li>
              <button onClick={() => handleDownload('PNG')}>Download PNG</button>
            </li>
            <li>
              <button onClick={() => handleDownload('CSV')}>Download CSV</button>
            </li>
          </ul>
        )}
      </div>
      <Bar data={chartData} options={options} />
    </div>
  );
};

export default CombinedBarChart;
