import React, { useState } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import { HorizontalBar } from 'react-chartjs-2';

const data = [
  { date: '2023-08-01', remediation: 10, totalInvocations: 50 },
  { date: '2023-08-02', remediation: 15, totalInvocations: 60 },
  // Add more data entries here
];

const HorizontalBarChartComponent = () => {
  const [selectedData, setSelectedData] = useState(null);

  const barChartData = {
    labels: data.map(entry => entry.date),
    datasets: [
      {
        label: 'Total Invocations',
        backgroundColor: '#42A5F5',
        data: data.map(entry => entry.totalInvocations),
      },
    ],
  };

  const onRowSelect = event => {
    setSelectedData(event.data);
  };

  return (
    <div>
      <h2>Data Table</h2>
      <DataTable value={data} selectionMode="single" onRowSelect={onRowSelect}>
        <Column field="date" header="Date"></Column>
        <Column field="remediation" header="Remediation"></Column>
        <Column field="totalInvocations" header="Total Invocations"></Column>
      </DataTable>

      <h2>Horizontal Bar Chart</h2>
      <HorizontalBar data={barChartData} />

      {selectedData && (
        <div>
          <h3>Selected Data:</h3>
          <p>Date: {selectedData.date}</p>
          <p>Remediation: {selectedData.remediation}</p>
          <p>Total Invocations: {selectedData.totalInvocations}</p>
        </div>
      )}
    </div>
  );
};

export default HorizontalBarChartComponent;
