import React from 'react';
import { Chart } from 'primereact/chart';

const PieChartComponent = ({ formData }) => {
  const getChartData = () => {
    if (!formData || formData.length === 0) {
      return {
        labels: [],
        datasets: [
          {
            data: [],
            backgroundColor: [],
          },
        ],
      };
    }

    const countries = formData.reduce((acc, data) => {
      acc[data.Country] = (acc[data.Country] || 0) + 1;
      return acc;
    }, {});

    const labels = Object.keys(countries);
    const data = Object.values(countries);
    const backgroundColor = [
      '#FF6384',
      '#36A2EB',
      '#FFCE56',
      '#008080',
      '#800080',
      '#FF0000',
      '#00FF00',
      '#0000FF',
    ].slice(0, labels.length);

    return {
      labels,
      datasets: [
        {
          data,
          backgroundColor,
        },
      ],
    };
  };

  const chartData = getChartData();

  const options = {
    responsive: true,
    maintainAspectRatio: false,
  };

  return (
    <div>
      <h3>Pie Chart</h3>
      {chartData.labels.length > 0 ? (
        <Chart type="pie" data={chartData} options={options} />
      ) : (
        <div>No data available for the chart.</div>
      )}
    </div>
  );
};

export default PieChartComponent;
