import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const SubMenuItem = ({ title, url, menuRouteTitle }) => {
  return (
    <li>
      <Link to={url}>{title}</Link>
    </li>
  );
};

const SubMenu = ({ submenu }) => {
  return (
    <ul>
      {submenu.map((item, index) => (
        <li key={index}>
          <SubMenuItem title={item.title} url={item.url} menuRouteTitle={item.menuRouteTitle} />
        </li>
      ))}
    </ul>
  );
};

const NavItem = ({ title, submenu, url }) => {
  const [active, setActive] = useState(false);

  const handleItemClick = () => {
    setActive(!active);
  };

  return (
    <div>
      <span onClick={handleItemClick}>{title}</span>
      {active && submenu && <SubMenu submenu={submenu} />}
      {url && active && <Link to={url}></Link>}
    </div>
  );
};

export default NavItem;
