import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import "primereact/resources/primereact.min.css";
import "primereact/resources/themes/tailwind-light/theme.css";  
import logo from './verizon_logo.jpg';

export const navItems = [
  {
    title: 'Dashboard',
    submenu: [
      {
        title: 'SOG Dashboard',
        url: '/seaofgreen',
        menuRouteTitle:'/seaofgreen',
        parentTitle:'Dashboard',
      },
      {
        title: 'Incident Dashboard',
        url: '/',
        menuRouteTitle:'/',
        parentTitle:'Dashboard',
      }
    ],
  },
  {
      title: 'Configuration',
      submenuType:'horizontal',
      submenu: [
        {
          title: 'Hawkeye configuration',
          parentTitle:'Configuration',
          submenu: [
            {
              title: 'Data Source',
              url: '/configuration',
              menuRouteTitle:'DataSource',
              tabData:{tab:'DataSource'},
              parentTitle:'Configuration',
            },
            {
              title: 'Application',
              url: '/configuration',
              menuRouteTitle:'Application',
              tabData:{tab:'Application'},
              parentTitle:'Configuration',
            },
            {
              title: 'Metric',
              url: '/configuration',
              tabData:{tab:'Metric'},
              menuRouteTitle:'Metric',
              parentTitle:'Configuration',
            },
            {
              title: 'Query',
              url: '/configuration',
              tabData:{tab:'Query'},
              menuRouteTitle:'Query',
              parentTitle:'Configuration',
            },
            {
              title: 'Threshold',
              url: '/configuration',
              menuRouteTitle:'Threshold',
              tabData:{tab:'Threshold'},
              parentTitle:'Configuration',
            },
            {
              title: 'Notifications',
              url: '/configuration',
              menuRouteTitle:'Notifications',
              tabData:{tab:'Notifications'},
              parentTitle:'Configuration',
            },
            {
              title: 'DC Failover',
              url: '/configuration',
              menuRouteTitle:'DcOverride',
              tabData:{tab:'DcOverride'},
              parentTitle:'Configuration',
            },
          ]
        },
        {
          title: 'SOG configuration',
          parentTitle:'Configuration',
          submenu:[
            {
              title: 'Define Section',
              url: '/sogConfiguration/sections',
              menuRouteTitle:'/sogConfiguration/sections',
              parentTitle:'Configuration',
            },
            {
              title: 'Define Channel',
              url: '/sogConfiguration/channels',
              menuRouteTitle:'/sogConfiguration/channels',
              parentTitle:'Configuration',
            },
            {
              title: 'Define Panel',
              url: '/sogConfiguration/panels',
              menuRouteTitle:'/sogConfiguration/panels',
              parentTitle:'Configuration',
            },
            {
              title: 'Configure Rules and Policies',
              url: '/rulesAndPolicyConfig/rules',
              menuRouteTitle:'/rulesAndPolicyConfig/rules',
              parentTitle:'Configuration',
            }
          ]
        }
      ],
    },
];

const SubMenuItem = ({ title, url, submenu }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isSubMenuOpen, setIsSubMenuOpen] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setIsSubMenuOpen(false);
  };

  const handleSubMenuClick = () => {
    setIsSubMenuOpen(!isSubMenuOpen);
    setIsHovered(false);
  };

  return (
    <li onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <Link
        to={url}
        style={{
          fontSize: '1rem', // Adjusted font size
          color: '#fff',
          textDecoration: 'none',
          padding: '0.5rem 1rem',
          backgroundColor: isHovered ? '#ff3f00' : 'transparent', // Verizon header color
          transition: 'background-color 0.3s, color 0.3s',
          fontWeight: isHovered ? 'bold' : 'normal',
        }}
        onClick={handleSubMenuClick}
      >
        {title}
        {submenu && <span style={{ marginLeft: '0.5rem' }}>{isSubMenuOpen ? '▼' : '►'}</span>}
      </Link>
      {submenu && isSubMenuOpen && (
        <ul
          style={{
            listStyle: 'none',
            margin: 0,
            padding: 0,
            position: 'absolute',
            top: 0,
            left: '100%',
            backgroundColor: '#ff3f00', // Verizon header color
            boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
            zIndex: 1,
            minWidth: '200px',
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          {submenu.map((item, index) => (
            <SubMenuItem key={index} title={item.title} url={item.url} submenu={item.submenu} />
          ))}
        </ul>
      )}
    </li>
  );
};

const NavItem = ({ title, submenu, url }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  return (
    <div
      style={{
        position: 'relative',
        padding: '0.5rem 1rem',
        cursor: 'pointer',
        fontWeight: isHovered ? 'bold' : 'normal',
      }}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      {title}
      {isHovered && submenu && (
        <ul
          style={{
            listStyle: 'none',
            margin: 0,
            padding: 0,
            position: 'absolute',
            top: '100%',
            left: 0,
            backgroundColor: '#ff3f00', // Verizon header color
            boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
            zIndex: 1,
            minWidth: '200px',
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          {submenu.map((item, index) => (
            <SubMenuItem key={index} title={item.title} url={item.url} submenu={item.submenu} />
          ))}
        </ul>
      )}
    </div>
  );
};

const Navigation = ({ navItems }) => {
  return (
    <div
      style={{
        backgroundColor: '#ff3f00', // Verizon header color
        color: '#fff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '1rem',
      }}
    >
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <img src={logo} alt="logo" style={{ height: '60px', marginRight: '10px' }} />
        <span style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>Verizon</span>
      </div>
      <ul style={{ listStyle: 'none', margin: 0, padding: 0, display: 'flex', alignItems: 'center' }}>
        {navItems.map((item, index) => (
          <li key={index} style={{ marginRight: '20px' }}>
            <NavItem title={item.title} submenu={item.submenu} url={item.url} />
          </li>
        ))}
      </ul>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Navigation navItems={navItems} />
    </Router>
  );
};


export default App;
