import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const CombinedBarChart = ({ data1, data2 }) => {
  const labels = data1.map(entry => entry.date);
  const datasets = [
    {
      label: 'Total Invocations (Data 1)',
      backgroundColor: 'rgba(66, 165, 245, 0.6)', // Blue color for Data 1
      data: data1.map(entry => entry.totalInvocations),
    },
    {
      label: 'Total Invocations (Data 2)',
      backgroundColor: 'rgba(255, 99, 132, 0.6)', // Red color for Data 2
      data: data2.map(entry => entry.totalInvocations),
    },
  ];

  const options = {
    indexAxis: 'y', // Use 'y' for vertical grouped bar chart
    plugins: {
      title: {
        display: true,
        text: 'Total Invocations',
        align: 'start',
        padding: {
          top: 10,
          left: 20,
        },
      },
    },
    scales: {
      x: {
        stacked: true,
        beginAtZero: true,
        title: {
          display: true,
          text: 'Remediation',
        },
      },
      y: {
        stacked: true,
        ticks: {
          autoSkip: false,
        },
      },
    },
  };

  return (
    <div className="combined-bar-chart">
      <Bar data={{ labels, datasets }} options={options} />
    </div>
  );
};

export default CombinedBarChart;
