import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';

const data = [
  { date: '2023-08-01', remediation: 10, totalInvocations: 50 },
  { date: '2023-08-02', remediation: 15, totalInvocations: 60 },
  // Add more data entries here
];

const HorizontalBarChart = () => {
  const [selectedData, setSelectedData] = useState(null);

  const chartData = {
    labels: data.map(entry => entry.date),
    datasets: [
      {
        label: 'Total Invocations',
        backgroundColor: '#42A5F5',
        data: data.map(entry => entry.totalInvocations),
      },
    ],
  };

  const options = {
    indexAxis: 'y', // Use 'y' for horizontal bar chart
    scales: {
      x: {
        beginAtZero: true,
      },
      y: {
        ticks: {
          autoSkip: false,
        },
      },
    },
    onClick: (_, elements) => {
      if (elements.length > 0) {
        const clickedIndex = elements[0].index;
        setSelectedData(data[clickedIndex]);
      }
    },
  };

  return (
    <div className="horizontal-bar-chart">
      <Bar data={chartData} options={options} />
      {selectedData && (
        <div className="selected-data">
          <h3>Selected Data:</h3>
          <p>Date: {selectedData.date}</p>
          <p>Remediation: {selectedData.remediation}</p>
          <p>Total Invocations: {selectedData.totalInvocations}</p>
        </div>
      )}
    </div>
  );
};

export default HorizontalBarChart;
