import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

const EditableTable = () => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [email, setEmail] = useState('');
  const [data, setData] = useState([]);

  const addRow = () => {
    if (!name || !age || !email) {
      alert("Please fill in all fields.");
      return;
    }

    setData((prevData) => [
      ...prevData,
      { name, age, email }
    ]);

    // Clear input fields after adding data to the table
    setName('');
    setAge('');
    setEmail('');
  };

  const onEditorValueChange = (props, value) => {
    const updatedData = [...data];
    updatedData[props.rowIndex][props.field] = value;
    setData(updatedData);
  };

  const inputTextEditor = (props) => (
    <InputText
      type="text"
      value={props.rowData[props.field]}
      onChange={(e) => onEditorValueChange(props, e.target.value)}
    />
  );

  return (
    <div>
      <h2>Editable Table</h2>
      <form>
        <label htmlFor="name">Name:</label>
        <InputText id="name" value={name} onChange={(e) => setName(e.target.value)} required />

        <label htmlFor="age">Age:</label>
        <InputText id="age" value={age} onChange={(e) => setAge(e.target.value)} required />

        <label htmlFor="email">Email:</label>
        <InputText id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

        <Button type="button" label="Add" onClick={addRow} className="p-button-primary" />
      </form>

      <DataTable value={data} editMode="row">
        <Column field="name" header="Name" editor={inputTextEditor} />
        <Column field="age" header="Age" editor={inputTextEditor} />
        <Column field="email" header="Email" editor={inputTextEditor} />
      </DataTable>
    </div>
  );
};

export default EditableTable;
