import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import "primereact/resources/primereact.min.css";
import "primereact/resources/themes/tailwind-light/theme.css";  
import logo from './verizon_logo.jpg';

export const navItems = [
  {
    title: 'Dashboard',
    submenu: [
      {
        title: 'SOG Dashboard',
        url: '/seaofgreen',
        menuRouteTitle:'/seaofgreen',
        parentTitle:'Dashboard',
      },
      {
        title: 'Incident Dashboard',
        url: '/',
        menuRouteTitle:'/',
        parentTitle:'Dashboard',
      }
    ],
  },
  {
      title: 'Configuration',
      submenuType:'horizontal',
      submenu: [
        {
          title: 'Hawkeye configuration',
          parentTitle:'Configuration',
          submenu: [
            {
              title: 'Data Source',
              url: '/configuration',
              menuRouteTitle:'DataSource',
              tabData:{tab:'DataSource'},
              parentTitle:'Configuration',
            },
            {
              title: 'Application',
              url: '/configuration',
              menuRouteTitle:'Application',
              tabData:{tab:'Application'},
              parentTitle:'Configuration',
            },
            {
              title: 'Metric',
              url: '/configuration',
              tabData:{tab:'Metric'},
              menuRouteTitle:'Metric',
              parentTitle:'Configuration',
            },
            {
              title: 'Query',
              url: '/configuration',
              tabData:{tab:'Query'},
              menuRouteTitle:'Query',
              parentTitle:'Configuration',
            },
            {
              title: 'Threshold',
              url: '/configuration',
              menuRouteTitle:'Threshold',
              tabData:{tab:'Threshold'},
              parentTitle:'Configuration',
            },
            {
              title: 'Notifications',
              url: '/configuration',
              menuRouteTitle:'Notifications',
              tabData:{tab:'Notifications'},
              parentTitle:'Configuration',
            },
            {
              title: 'DC Failover',
              url: '/configuration',
              menuRouteTitle:'DcOverride',
              tabData:{tab:'DcOverride'},
              parentTitle:'Configuration',
            },
          ]
        },
        {
          title: 'SOG configuration',
          parentTitle:'Configuration',
          submenu:[
            {
              title: 'Define Section',
              url: '/sogConfiguration/sections',
              menuRouteTitle:'/sogConfiguration/sections',
              parentTitle:'Configuration',
            },
            {
              title: 'Define Channel',
              url: '/sogConfiguration/channels',
              menuRouteTitle:'/sogConfiguration/channels',
              parentTitle:'Configuration',
            },
            {
              title: 'Define Panel',
              url: '/sogConfiguration/panels',
              menuRouteTitle:'/sogConfiguration/panels',
              parentTitle:'Configuration',
            },
            {
              title: 'Configure Rules and Policies',
              url: '/rulesAndPolicyConfig/rules',
              menuRouteTitle:'/rulesAndPolicyConfig/rules',
              parentTitle:'Configuration',
            }
          ]
        }
      ],
    },
];

const SubMenuItem = ({ title, url, submenu }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isSubMenuOpen, setIsSubMenuOpen] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setIsSubMenuOpen(false); // Close submenu on mouse leave
  };

  const handleSubMenuClick = () => {
    setIsSubMenuOpen(!isSubMenuOpen);
    setIsHovered(false); // Close submenu after clicking any submenu item
  };


  return (
    <li style={{ listStyleType: 'none'}} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <Link
        to={url}
        // ... (same as your existing code)
      >
        {title}
        {submenu && <span style={{ marginLeft: '0.5rem' }}>{isSubMenuOpen ? '>' : '>'}</span>}
      </Link>
      {submenu && isSubMenuOpen && (
        <div
          className="submenu-container"
          style={{
            position: 'absolute',
            top: 0,
            left: '100%',
            zIndex: 1,
            width: '100%',
            backgroundColor: '#000',
          }}
        >
          {submenu.map((item, index) => (
            <SubMenuItem key={index} title={item.title} url={item.url} submenu={item.submenu} />
          ))}
        </div>
      )}
    </li>
  );
};

const NavItem = ({ title, submenu, url }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  return (
    <div
      style={{
        marginRight: '1rem',
        position: 'relative',
        // ... (Other styling for the main menu item)
      }}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <span
        style={{
          fontSize: '1.5rem',
          marginLeft: '1rem',
          cursor: 'pointer',
          fontWeight: 700,
        }}
      >
        {title}
      </span>
      {url && <Link to={url}></Link>}
      {isHovered && submenu && (
        <ul
          className="submenu-container"
	  style={{
            top: '100%', // Adjusted positioning
            left: 0, // Adjusted positioning
          }}

          // ... (Other styling for the submenu container)
        >
          {submenu.map((item, index) => (
            <SubMenuItem key={index} title={item.title} url={item.url} submenu={item.submenu} />
          ))}
        </ul>
      )}
    </div>
  );
};

const Navigation = ({ navItems }) => {
  const [selectedMenuItemIndex, setSelectedMenuItemIndex] = useState(null);
  const [selectedSubmenuItemIndex, setSelectedSubmenuItemIndex] = useState(null);

  const handleMenuHover = (index) => {
    setSelectedMenuItemIndex(index);
  };

  const handleSubmenuHover = (index) => {
    setSelectedSubmenuItemIndex(index);
  };

  const handleMouseLeave = () => {
    setSelectedMenuItemIndex(null);
    setSelectedSubmenuItemIndex(null);
  };
  return (
    <div
      style={{
        backgroundColor: '#000',
        color: '#fff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '1rem',
        position: 'relative',
        zIndex: 1,
        transition: 'background-color 0.3s',
      }}
    >
      <div>
        {/* ... (same as your existing code) */}
      </div>
      {selectedMenuItemIndex !== null && (
        <div
          style={{
            position: 'absolute',
            top: '100%',
            left: 0,
            zIndex: 0,
            width: '100%',
            backgroundColor: '#000',
            padding: '1rem',
          }}
        >
          {navItems[selectedMenuItemIndex]?.submenu.map((item, index) => (
            <li
              key={index}
              style={{ margin: '0.5rem 0', position: 'relative' }}
            >
              <SubMenuItem title={item.title} url={item.url} submenu={item.submenu} />
            </li>
          ))}
        </div>
      )}
      {selectedSubmenuItemIndex !== null && (
        <div style={{ marginTop: '1rem' }}>
          {/* ... (same as your existing code) */}
        </div>
      )}
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Navigation navItems={navItems} />
    </Router>
  );
};

export default App;
