import React, { useEffect, useRef } from 'react';
import { Bar } from 'react-chartjs-2';

const MultiBarGraph = ({ dataTables }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      const datasets = [];
      let labels = [];

      dataTables.forEach((table, index) => {
        const data = table.map(row => row.totalInvocations);
        labels = table.map(row => row.date);
        const color = getRandomColor();

        datasets.push({
          label: `Remediation ${index + 1}`,
          data,
          backgroundColor: color,
          borderColor: color,
          borderWidth: 1,
        });
      });

      const data = {
        labels,
        datasets,
      };

      // No need to create a new Chart instance here

      // Update the chart data and options using the ref
      chartRef.current.data = data;
      chartRef.current.options = options;
      chartRef.current.update();
    }
  }, [dataTables]);

  return <Bar ref={chartRef} />;
};

const options = {
  scales: {
    x: {
      type: 'category',
      stacked: true,
    },
    y: {
      stacked: true,
    },
  },
};

function getRandomColor() {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

export default MultiBarGraph;
