import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';

const MultiBarGraph = ({ dataTables }) => {
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    if (dataTables && dataTables.length > 0) {
      const datasets = [];
      let labels = [];
      console.log(dataTables, datasets)

      dataTables.forEach((table, index) => {
        const data = table.map?.(row => row.totalInvocations);
        labels = table.map(row => row.date);
        const color = getRandomColor();

        datasets.push({
          label: `Remediation ${index + 1}`,
          data,
          backgroundColor: color,
          borderColor: color,
          borderWidth: 1,
        });
      });

      const data = {
        labels,
        datasets,
      };

      setChartData(data);
    }
  }, [dataTables]);

  return (
    <div>
      <h1>Multi Bar Graph Example</h1>
      <Bar data={chartData} options={options} />
    </div>
  );
};

const options = {
  scales: {
    x: {
      type: 'category',
      stacked: true,
    },
    y: {
      stacked: true,
    },
  },
};

function getRandomColor() {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

export default MultiBarGraph;
