import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import "primereact/resources/primereact.min.css";
import "primereact/resources/themes/tailwind-light/theme.css";  
import logo from './verizon_logo.jpg';

export const navItems = [
  {
    title: 'Dashboard',
    submenu: [
      {
        title: 'SOG Dashboard',
        url: '/seaofgreen',
        menuRouteTitle:'/seaofgreen',
        parentTitle:'Dashboard',
      },
      {
        title: 'Incident Dashboard',
        url: '/',
        menuRouteTitle:'/',
        parentTitle:'Dashboard',
      }
    ],
  },
  {
      title: 'Configuration',
      submenuType:'horizontal',
      submenu: [
        {
          title: 'Hawkeye configuration',
          parentTitle:'Configuration',
          submenu: [
            {
              title: 'Data Source',
              url: '/configuration',
              menuRouteTitle:'DataSource',
              tabData:{tab:'DataSource'},
              parentTitle:'Configuration',
            },
            {
              title: 'Application',
              url: '/configuration',
              menuRouteTitle:'Application',
              tabData:{tab:'Application'},
              parentTitle:'Configuration',
            },
            {
              title: 'Metric',
              url: '/configuration',
              tabData:{tab:'Metric'},
              menuRouteTitle:'Metric',
              parentTitle:'Configuration',
            },
            {
              title: 'Query',
              url: '/configuration',
              tabData:{tab:'Query'},
              menuRouteTitle:'Query',
              parentTitle:'Configuration',
            },
            {
              title: 'Threshold',
              url: '/configuration',
              menuRouteTitle:'Threshold',
              tabData:{tab:'Threshold'},
              parentTitle:'Configuration',
            },
            {
              title: 'Notifications',
              url: '/configuration',
              menuRouteTitle:'Notifications',
              tabData:{tab:'Notifications'},
              parentTitle:'Configuration',
            },
            {
              title: 'DC Failover',
              url: '/configuration',
              menuRouteTitle:'DcOverride',
              tabData:{tab:'DcOverride'},
              parentTitle:'Configuration',
            },
          ]
        },
        {
          title: 'SOG configuration',
          parentTitle:'Configuration',
          submenu:[
            {
              title: 'Define Section',
              url: '/sogConfiguration/sections',
              menuRouteTitle:'/sogConfiguration/sections',
              parentTitle:'Configuration',
            },
            {
              title: 'Define Channel',
              url: '/sogConfiguration/channels',
              menuRouteTitle:'/sogConfiguration/channels',
              parentTitle:'Configuration',
            },
            {
              title: 'Define Panel',
              url: '/sogConfiguration/panels',
              menuRouteTitle:'/sogConfiguration/panels',
              parentTitle:'Configuration',
            },
            {
              title: 'Configure Rules and Policies',
              url: '/rulesAndPolicyConfig/rules',
              menuRouteTitle:'/rulesAndPolicyConfig/rules',
              parentTitle:'Configuration',
            }
          ]
        }
      ],
    },
];


const SubMenuItem = ({ title, url, submenu }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  return (
    <li
      style={{
        listStyleType: 'none',
        width: '20rem', // Adjust the width as needed to accommodate 30 characters
        margin: '0.25rem 0',
      }}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <Link
        to={url}
        style={{
          display: 'flex',
          justifyContent: 'space-between', // Aligns the arrow to the right
          alignItems: 'center',
          fontSize: '1.2rem', // Reduced font size
          color: isHovered ? '#fff' : '#fff',
          textDecoration: 'none',
          padding: '0.25rem 0.5rem', // Reduced padding
          backgroundColor: isHovered ? 'rgba(255, 255, 255, 0.1)' : 'transparent',
          transition: 'background-color 0.3s, color 0.3s',
          fontWeight: isHovered ? 700 : 'normal',
        }}
      >
        <span>{title}</span>
        {submenu && <span>></span>}
      </Link>
    </li>
  );
};

const NavItem = ({ title, submenu, url }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isSubmenuOpen, setIsSubmenuOpen] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setIsSubmenuOpen(false);
  };

  const handleMenuClick = () => {
    setIsSubmenuOpen(!isSubmenuOpen);
  };

  return (
    <div
      style={{
        marginRight: '1rem',
        position: 'relative',
      }}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <span
        style={{
          fontSize: '1.5rem',
          marginLeft: '1rem',
          cursor: 'pointer',
          fontWeight: 700,
        }}
        onClick={handleMenuClick} // Click handler to toggle the submenu
      >
        {title}
      </span>
      {url && <Link to={url}></Link>}
      {isHovered && isSubmenuOpen && submenu && (
        <ul
          className="submenu-container"
          style={{
            top: 0,
            left: '100%',
          }}
        >

        </ul>
      )}
    </div>
  );
};

const Navigation = ({ navItems }) => {
  const [selectedMenuItemIndex, setSelectedMenuItemIndex] = useState(null);
  const [selectedSubmenuItemIndex, setSelectedSubmenuItemIndex] = useState(null);

  const handleMenuClick = (index) => {
    setSelectedMenuItemIndex(index);
    setSelectedSubmenuItemIndex(null); // Close any open sub-submenu
  };

  const handleSubmenuClick = (index) => {
    setSelectedSubmenuItemIndex(index);
  };

  return (
    <div
      style={{
        backgroundColor: '#000',
        color: '#fff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '1rem',
        position: 'relative',
        zIndex: 1,
        transition: 'background-color 0.3s',
      }}
    >
      <div>
        <span style={{ fontSize: '2.5rem', fontWeight: 'bold', display: 'flex', alignItems: 'center' }}>
          <img src={logo} alt="logo" style={{ height: '100px', marginRight: '10px' }} />
          <span style={{ fontSize: '1.5rem', marginLeft: '10rem' }}>
            <ul style={{ listStyle: 'none', margin: 0, padding: 0, display: 'flex' }}>
              {navItems.map((item, index) => (
                <li
                  key={index}
                  onClick={() => handleMenuClick(index)}
                  style={{ position: 'relative' }}
                >
                  <NavItem title={item.title} submenu={item.submenu} url={item.url} />
                </li>
              ))}
            </ul>
          </span>
        </span>
      </div>
      {selectedMenuItemIndex !== null && (
        <div
          style={{
            position: 'absolute',
            top: '100%',
            left: 0,
            zIndex: 0,
            backgroundColor: '#000',
            padding: '1rem',
            width: '100%',
            display: 'flex', // Flex container to arrange submenu and options side by side
          }}
        >
	<h2 style={{ color: '#fff', margin: '11rem' }}>
            {navItems[selectedMenuItemIndex]?.title}
          </h2>
          <div
            style={{
              marginRight: '2rem', // Add spacing between options and submenu
            }}
          >
            <ul style={{ listStyle: 'none', margin: '0', padding: 0 }}>
              {navItems[selectedMenuItemIndex]?.submenu.map((item, index) => (
                <li
                  key={index}
                  onClick={() => handleSubmenuClick(index)}
                  style={{ margin: '0.5rem 0', position: 'relative' }}
                >
                  <SubMenuItem
                    title={item.title}
                    url={item.url}
                    submenu={item.submenu}
                  />
                </li>
              ))}
            </ul>
          </div>
          <div
            style={{
              backgroundColor: '#000',
              padding: '1rem',
              minWidth: '12rem',
            }}
          >
            {navItems[selectedMenuItemIndex]?.submenu[selectedSubmenuItemIndex]?.submenu.map(
              (submenuItem, subIndex) => (
                <SubMenuItem
                  key={subIndex}
                  title={submenuItem.title}
                  url={submenuItem.url}
                  submenu={submenuItem.submenu}
                  parentTitle={
                    submenuItem.parentTitle ||
                    navItems[selectedMenuItemIndex]?.submenu[selectedSubmenuItemIndex]?.parentTitle
                  }
                />
              )
            )}
          </div>
        </div>
      )}
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Navigation navItems={navItems} />
    </Router>
  );
};

export default App;
