import React from 'react';
import DataTableComponent from './DataTableComponent';
import MenuBarComponent from './MenuBarComponent';
import PieChartComponent from './PieChartComponent';

function App() {
  // Sample form data
  const formData = [
    { id: 1, Name: 'John Doe', mailId: 'john@example.com', Profile: 'Engineer', Country: 'USA' },
    { id: 2, Name: 'Jane Smith', mailId: 'jane@example.com', Profile: 'Designer', Country: 'Canada' },
    { id: 1, Name: 'John Doe', mailId: 'john@example.com', Profile: 'Engineer', Country: 'USA' },
    { id: 2, Name: 'Jane Smith', mailId: 'jane@example.com', Profile: 'Designer', Country: 'Canada' },
    // Add more data here
  ];

  return (
    <div>
      <MenuBarComponent />
      <DataTableComponent formData={formData} />
      <PieChartComponent data={formData} />
    </div>
  );
}

export default App;
