import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import { saveAs } from 'file-saver';
import './CombinedBarChart.css';
import { Button } from 'primereact/button'; // Import PrimeReact Button component
import { Menubar } from 'primereact/menubar'; // Import PrimeReact Menubar component

Chart.register(...registerables);

const CombinedBarChart = ({ datasets }) => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const downloadChart = (format) => {
    const canvas = document.querySelector('.combined-bar-chart canvas');
    if (!canvas) return;

    canvas.toBlob(function (blob) {
      saveAs(blob, `chart.${format}`);
    }, `image/${format}`);
  };

  const dates = datasets[0].data.map(entry => entry.date);
  const chartData = {
    labels: dates,
    datasets: datasets.map(dataset => ({
      label: dataset.label,
      backgroundColor: dataset.backgroundColor,
      data: dataset.data.map(entry => entry.totalInvocations),
    })),
  };

  const options = {
    indexAxis: 'y',
    plugins: {
      legend: {
        position: 'right',
      },
    },
    scales: {
      y: {
        stacked: true,
        ticks: {
          autoSkip: false,
        },
      },
      x: {
        stacked: true,
        beginAtZero: true,
        title: {
          display: true,
          text: 'REMEDIATION',
        },
      },
    },
  };

  const items = [
    {
      label: 'Download',
      icon: 'pi pi-download',
      items: [
        {
          label: 'SVG',
          icon: 'pi pi-image',
          command: () => downloadChart('svg'),
        },
        {
          label: 'PNG',
          icon: 'pi pi-image',
          command: () => downloadChart('png'),
        },
        // Add CSV download option
      ],
    },
  ];

  return (
    <div className="combined-bar-chart">
      <Menubar
        model={items}
        end={
          <Button icon="pi pi-bars" onClick={toggleDropdown} />
        }
      />
      {isDropdownOpen && (
        <div className="dropdown-content">
          {/* Include your download options here */}
        </div>
      )}
      <Bar data={chartData} options={options} />
    </div>
  );
};

export default CombinedBarChart;
