import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const CombinedBarChart = ({ datasets }) => {
  const dates = datasets[0].data.map(entry => entry.date);

  const options = {
    indexAxis: 'y',
    plugins: {
      title: {
        display: true,
        text: 'Total Invocations',
        align: 'start',
        padding: {
          top: 10,
          left: 20,
        },
      },
      legend: {
        position: 'right',
      },
    },
    scales: {
      x: {
        stacked: true,
        beginAtZero: true,
        title: {
          display: true,
          text: 'Remediation',
        },
      },
      y: {
        stacked: true,
        ticks: {
          autoSkip: false,
        },
        type: 'time',
        time: {
          unit: 'day', // Display dates on y-axis scale
          displayFormats: {
            day: 'MMM DD', // Format of the displayed dates
          },
        },
      },
    },
  };

  return (
    <div className="combined-bar-chart">
      <Bar data={{ labels: dates, datasets }} options={options} />
    </div>
  );
};

export default CombinedBarChart;
