import logo from './logo.svg';
import './App.css';
import React, { useState, useRef } from "react";

import {  InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { Toast } from 'primereact/toast';

//theme
import "primereact/resources/themes/vela-Blue/theme.css";     

//core
import "primereact/resources/primereact.min.css";                                       

import "primeicons/primeicons.css"

function App() {
  const [text,setText] = useState('');
  const toastRef = useRef();
  const onButtonClick = () => {
    if(text)
      toastRef.current.show({severity:'info', summary : 'Success', detail: text})
    else
      toastRef.current.show({severity:'error', summary : 'Error', detail: 'Value is Required'})

  }
  return (
    <div className="App">
      <Toast ref={toastRef} />
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <InputText value={text} onChange = { e=>setText(e.target.value)}/>
        <br/>
        <Button type="button" label="Submit" icon="pi pi-check" onClick = {onButtonClick}></Button>
      </header>
    </div>
  );
}

export default App;
