import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import "primereact/resources/primereact.min.css";
import "primereact/resources/themes/tailwind-light/theme.css";  
import logo from './verizon_logo.jpg';

export const navItems = [
  {
    title: 'Dashboard',
    submenu: [
      {
        title: 'SOG Dashboard',
        url: '/seaofgreen',
        menuRouteTitle:'/seaofgreen',
        parentTitle:'Dashboard',
      },
      {
        title: 'Incident Dashboard',
        url: '/',
        menuRouteTitle:'/',
        parentTitle:'Dashboard',
      }
    ],
  },
  {
      title: 'Configuration',
      submenuType:'horizontal',
      submenu: [
        {
          title: 'Hawkeye configuration',
          parentTitle:'Configuration',
          submenu: [
            {
              title: 'Data Source',
              url: '/configuration',
              menuRouteTitle:'DataSource',
              tabData:{tab:'DataSource'},
              parentTitle:'Configuration',
            },
            {
              title: 'Application',
              url: '/configuration',
              menuRouteTitle:'Application',
              tabData:{tab:'Application'},
              parentTitle:'Configuration',
            },
            {
              title: 'Metric',
              url: '/configuration',
              tabData:{tab:'Metric'},
              menuRouteTitle:'Metric',
              parentTitle:'Configuration',
            },
            {
              title: 'Query',
              url: '/configuration',
              tabData:{tab:'Query'},
              menuRouteTitle:'Query',
              parentTitle:'Configuration',
            },
            {
              title: 'Threshold',
              url: '/configuration',
              menuRouteTitle:'Threshold',
              tabData:{tab:'Threshold'},
              parentTitle:'Configuration',
            },
            {
              title: 'Notifications',
              url: '/configuration',
              menuRouteTitle:'Notifications',
              tabData:{tab:'Notifications'},
              parentTitle:'Configuration',
            },
            {
              title: 'DC Failover',
              url: '/configuration',
              menuRouteTitle:'DcOverride',
              tabData:{tab:'DcOverride'},
              parentTitle:'Configuration',
            },
          ]
        },
        {
          title: 'SOG configuration',
          parentTitle:'Configuration',
          submenu:[
            {
              title: 'Define Section',
              url: '/sogConfiguration/sections',
              menuRouteTitle:'/sogConfiguration/sections',
              parentTitle:'Configuration',
            },
            {
              title: 'Define Channel',
              url: '/sogConfiguration/channels',
              menuRouteTitle:'/sogConfiguration/channels',
              parentTitle:'Configuration',
            },
            {
              title: 'Define Panel',
              url: '/sogConfiguration/panels',
              menuRouteTitle:'/sogConfiguration/panels',
              parentTitle:'Configuration',
            },
            {
              title: 'Configure Rules and Policies',
              url: '/rulesAndPolicyConfig/rules',
              menuRouteTitle:'/rulesAndPolicyConfig/rules',
              parentTitle:'Configuration',
            }
          ]
        }
      ],
    },
];

const SubMenuItem = ({ title, url, submenu }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [submenuOpen, setSubmenuOpen] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  const handleClick = () => {
    setSubmenuOpen(!submenuOpen);
  };

  return (
    <li>
      <span
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        onClick={handleClick} // Add onClick handler
        style={{
          fontSize: '1.2rem',
          color: isHovered ? '#fff' : '#fff',
          textDecoration: 'none',
          display: 'block',
          padding: '0.5rem 1rem',
          fontWeight: isHovered ? 'bold' : 'normal',
          backgroundColor: isHovered ? 'rgba(255, 255, 255, 0.1)' : 'transparent',
          transition: 'background-color 0.3s, color 0.3s, font-weight 0.3s',
          cursor: 'pointer', // Add cursor style
        }}
      >
        {title}
      </span>
      {submenu && (
        <div
          style={{
            position: 'absolute',
            left: '100%',
            top: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.8)',
            color: '#fff',
            boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
            minWidth: '200px',
            zIndex: 1,
            transition: 'opacity 0.3s, transform 0.3s',
            transform: `scaleX(${submenuOpen ? 1 : 0})`, // Use submenuOpen state
            transformOrigin: 'top left',
            maxHeight: submenuOpen ? '1000px' : '0',
            overflow: 'hidden',
          }}
        >
          <ul style={{ listStyle: 'none', margin: 0, padding: 0 }}>
            {submenu.map((item, index) => (
              <SubMenuItem key={index} title={item.title} url={item.url} submenu={item.submenu} />
            ))}
          </ul>
        </div>
      )}
      {url && <Link to={url}></Link>}
    </li>
  );
};

const NavItem = ({ title, submenu, submenuType, url }) => {
  const [hovered, setHovered] = useState(false);
  const [submenuOpen, setSubmenuOpen] = useState(false);

  const handleMouseEnter = () => {
    setHovered(true);
  };

  const handleMouseLeave = () => {
    setHovered(false);
  };

  const handleClick = () => {
    setSubmenuOpen(!submenuOpen);
  };

  return (
    <div
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onClick={handleClick} // Add onClick handler
      style={{ display: 'inline-block', marginRight: '1rem', position: 'relative' }}
    >
      <span style={{ fontSize: '1.5rem', marginLeft: '1rem', cursor: 'pointer', fontWeight: 700 }}>
        {title}
      </span>
      {submenu && (
        <div
          style={{
            position: 'absolute',
            left: '100%',
            top: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.8)',
            color: '#fff',
            boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
            minWidth: '200px',
            zIndex: 1,
            transition: 'opacity 0.3s, transform 0.3s',
            transform: `scaleX(${submenuOpen ? 1 : 0})`, // Use submenuOpen state
            transformOrigin: 'top left',
            maxHeight: submenuOpen ? '1000px' : '0',
            overflow: 'hidden',
          }}
        >
          <ul style={{ listStyle: 'none', margin: 0, padding: 0 }}>
            {submenu.map((item, index) => (
              <SubMenuItem key={index} title={item.title} url={item.url} hovered={hovered} submenu={item.submenu} />
            ))}
          </ul>
        </div>
      )}
      {url && <Link to={url}></Link>}
    </div>
  );
};

const headerStyles = {
  backgroundColor: '#000',
  color: '#fff',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '1rem',
};

const logoStyles = {
  height: '100px',
  marginRight: '10px',
};

const Navigation = () => {
  return (
    <div style={headerStyles}>
      <div>
        <span style={{ fontSize: '2.5rem', fontWeight: 'bold', display: 'flex', alignItems: 'center' }}>
          <img src={logo} alt="logo" style={logoStyles} />
          <span>
            <span style={{ fontSize: '1.5rem', marginLeft: '1rem' }}>
              {navItems.map((item, index) => (
                <NavItem key={index} title={item.title} submenu={item.submenu} url={item.url} />
              ))}
            </span>
          </span>
        </span>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Navigation />
    </Router>
  );
};

export default App;
