import React, {useEffect, useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
// import logo from './logo.svg';
import "./App.css";
import Profile from "./components/Profile";

//theme
import "primereact/resources/themes/vela-blue/theme.css";     
//core
import "primereact/resources/primereact.min.css";                                              
import "primeicons/primeicons.css"

function App() {

  const [profileData, setProfileData] = useState([]);
  useEffect(() => {
    fetch("http://localhost:3000/profileData")
    .then((resp)=>resp.json())
    .then((data)=> {
      setProfileData(data)
    });
  },[]);

  function onUpdateProfileData(updatedProfileData){
    const updatedProfile=profileData.map(
      profile => {
        if(profile.id === updatedProfileData.id){
          return updatedProfileData
        } else {return profile}
        }
    )
    setProfileData(updatedProfileData)
  }

  return (
    <div className="App p-input-filled">
      <Profile profileData={profileData} onUpdatedProfileData={onUpdateProfileData}/>      
      <header className="App-header">
        {/* <img src={logo} className="App-logo" alt="logo" /> */}
        <h2>Add Form Data to Table</h2>
        <Profile />
      </header>
    </div>
  );
}
export default App;
