import React, { useState } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';

const DataTableComponent = () => {
  const [formData, setFormData] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);

  const editRow = (rowData) => {
    setSelectedRow(rowData);
  };

  const onInputChange = (event) => {
    setSelectedRow((prevRow) => ({
      ...prevRow,
      [event.target.name]: event.target.value,
    }));
  };

  const onRowEditSave = (event) => {
    setFormData((prevData) =>
      prevData.map((data) => (data.id === selectedRow.id ? selectedRow : data))
    );
    setSelectedRow(null);
  };

  const onRowEditCancel = (event) => {
    setSelectedRow(null);
  };

  const actionTemplate = (rowData) => (
    <>
      {selectedRow === rowData ? (
        // <button className="p-button p-button-success" onClick={onRowEditSave}>
        //   Save
        // </button>
        <div className="d-grid">
        <Button type="button" label="Submit" icon="pi pi-check" onClick = {onRowEditSave}></Button>
      </div>
      ) : (
        <button className="p-button p-button-secondary" onClick={() => editRow(rowData)}>
          Edit
        </button>
      )}
    </>
  );

      

  const formDataColumns = [
    { field: 'Name', header: 'Name' },
    { field: 'mailId', header: 'Mail Id' },
    { field: 'Profile', header: 'Profile' },
    { field: 'Country', header: 'Country' },
    { field: 'Action', header: 'Action', body: actionTemplate },
  ];

  return (
    <div>
      <DataTable
        value={formData}
        editable={true}
        editMode="row"
        onRowEditInit={(event) => setSelectedRow(event.data)}
        onRowEditCancel={onRowEditCancel}
        paginator
        rows={5}
        rowsPerPageOptions={[5, 10, 20]}
      >
        {formDataColumns.map((col) => (
          <Column
            key={col.field}
            field={col.field}
            header={col.header}
            editable={selectedRow !== null}
            editor={
              selectedRow !== null ? (
                <input
                  type="text"
                  name={col.field}
                  value={selectedRow[col.field] || ''}
                  onChange={onInputChange}
                />
              ) : null
            }
          />
        ))}
      </DataTable>
    </div>
  );
};

export default DataTableComponent;
