import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const CombinedBarChart = ({ datasets }) => {
  const chartData = {
    labels: datasets[0].data.map(entry => entry.date),
    datasets: datasets.map((dataset, index) => ({
      label: `Total Invocations (Data ${index + 1})`,
      backgroundColor: dataset.color, // Use the specified color for the dataset
      data: dataset.data.map(entry => entry.totalInvocations),
    })),
  };

  const options = {
    indexAxis: 'y', // Use 'y' for vertical grouped bar chart
    plugins: {
      title: {
        display: true,
        text: 'Total Invocations',
        align: 'start',
        padding: {
          top: 10,
          left: 20,
        },
      },
    },
    scales: {
      x: {
        stacked: true,
        beginAtZero: true,
      },
      y: {
        stacked: true,
        ticks: {
          autoSkip: false,
        },
      },
    },
  };

  return (
    <div className="combined-bar-chart">
      <Bar data={chartData} options={options} />
    </div>
  );
};

export default CombinedBarChart;
