import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import { saveAs } from 'file-saver';
import './CombinedBarChart.css';
import { Button } from 'primereact/button'; // Import PrimeReact Button component
import { Menubar } from 'primereact/menubar'; // Import PrimeReact Menubar component

Chart.register(...registerables);
// ... (previous code)
// import { saveAs } from 'file-saver'; // Import FileSaver for saving files

const CombinedBarChart = ({ datasets }) => {
  const downloadChart = (format) => {
    const canvas = document.querySelector('.combined-bar-chart canvas');
    if (!canvas) return;

    canvas.toBlob(function (blob) {
      saveAs(blob, `chart.${format}`);
    }, `image/${format}`);
  };

  const items = [
    {
      label: 'SVG',
      icon: 'pi pi-image',
      command: () => downloadChart('svg'),
    },
    {
      label: 'PNG',
      icon: 'pi pi-image',
      command: () => downloadChart('png'),
    },
    // Add more download options
  ];

  return (
    <div className="combined-bar-chart">
      <Button
        icon="pi pi-bars"
        onClick={() => downloadChart('png')} // Change this to your preferred format
      />
      <Bar data={chartData} options={options} />
    </div>
  );
};

export default CombinedBarChart;
