import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';

const PieChartComponent = ({ formData }) => {
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    const countryData = formData.reduce((acc, curr) => {
      acc[curr.Country] = (acc[curr.Country] || 0) + 1;
      return acc;
    }, {});

    setChartData({
      labels: Object.keys(countryData),
      datasets: [
        {
          data: Object.values(countryData),
          backgroundColor: [
            '#42A5F5',
            '#66BB6A',
            '#FFA726',
            '#7E57C2',
            '#FF7043',
            '#5C6BC0',
            '#FDD835',
          ],
          hoverBackgroundColor: [
            '#64B5F6',
            '#81C784',
            '#FFB74D',
            '#9575CD',
            '#FF8A65',
            '#7986CB',
            '#FBC02D',
          ],
        },
      ],
    });
  }, [formData]);

  return (
    <div key={JSON.stringify(chartData)}>
      <h3>Pie Chart</h3>
      <Pie data={chartData} />
    </div>
  );
};

export default PieChartComponent;
