import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Chart } from 'primereact/chart';

const DataTableComponent = () => {
  const [formData, setFormData] = useState([]);
  const [editingRows, setEditingRows] = useState({});
  const [newData, setNewData] = useState({
    id: null,
    Name: '',
    mailId: '',
    Profile: '',
    Country: '',
  });

  useEffect(() => {
    setFormData(formData || []);
  }, [formData, setFormData]);

  const editRow = (rowData) => {
    setEditingRows((prevState) => ({ ...prevState, [rowData.id]: true }));
  };

  const cancelEditRow = (rowData) => {
    if (!rowData.id) {
      setFormData((prevData) => prevData.filter((item) => item.id !== null)); // Remove the empty row on cancel
    }
    setEditingRows((prevState) => ({ ...prevState, [rowData.id]: false }));
  };

  const saveEditRow = (rowData) => {
    setEditingRows((prevState) => ({ ...prevState, [rowData.id]: false }));
    // You can add logic here to save the edited data, e.g., send it to the backend.
  };

  const deleteRow = (rowData) => {
    // You can add logic here to delete the row, e.g., send a delete request to the backend.
    // In this example, we'll remove the row from the local state.
    setFormData((prevData) => prevData.filter((item) => item.id !== rowData.id));
  };

  const addRow = () => {
    if (!newData.Name || !newData.mailId || !newData.Profile || !newData.Country) {
      return;
    }

    // Generate a unique ID for the new row (this can be done differently depending on your requirements).
    const newId = formData.length + 1;
    setFormData((prevData) => [...prevData, { ...newData, id: newId }]);
    setEditingRows((prevState) => ({ ...prevState, [newId]: true })); // Enable edit mode for the newly added row
    setNewData({ // Clear the new data for the next entry
      id: null,
      Name: '',
      mailId: '',
      Profile: '',
      Country: '',
    });
  };

  const actionEditorTemplate = (rowData) => {
    if (editingRows[rowData.id]) {
      return (
        <div>
          <button className="p-button p-button-success p-mr-2" onClick={() => saveEditRow(rowData)}>
            Save
          </button>
          <button className="p-button p-button-danger" onClick={() => cancelEditRow(rowData)}>
            Cancel
          </button>
        </div>
      );
    } else {
      return (
        <div>
          <button className="p-button p-button-primary" onClick={() => editRow(rowData)}>
            Edit
          </button>
          <button className="p-button p-button-danger p-ml-2" onClick={() => deleteRow(rowData)}>
            Delete
          </button>
        </div>
      );
    }
  };

  const onEditorValueChange = (props, value) => {
    setNewData((prevData) => ({ ...prevData, [props.field]: value }));
  };

  const editorInputText = (props) => {
    return (
      <input
        type="text"
        value={props.rowData[props.field]}
        onChange={(e) => onEditorValueChange(props, e.target.value)}
        placeholder={props.field}
      />
    );
  };

  const columns = [
    { field: 'id', header: 'ID' },
    { field: 'Name', header: 'Name', editor: editorInputText },
    { field: 'mailId', header: 'Mail Id', editor: editorInputText },
    { field: 'Profile', header: 'Profile', editor: editorInputText },
    { field: 'Country', header: 'Country', editor: editorInputText },
    { field: 'action', header: 'Action', body: actionEditorTemplate, editor: actionEditorTemplate },
  ];

  const pieChartData = {
    labels: formData.map((item) => item.Country),
    datasets: [
      {
        data: formData.map((item) => 1), // Set the data to 1 for each row (you can update it based on your data)
        backgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56',
          '#4BC0C0',
          '#9966FF',
          '#FF9F40',
          '#00BFFF',
        ],
      },
    ],
  };

  return (
    <div>
      <h3>DataTable</h3>
      <div>
        <input
          type="text"
          value={newData.Name}
          onChange={(e) => setNewData({ ...newData, Name: e.target.value })}
          placeholder="Name"
        />
        <input
          type="text"
          value={newData.mailId}
          onChange={(e) => setNewData({ ...newData, mailId: e.target.value })}
          placeholder="Mail Id"
        />
        <input
          type="text"
          value={newData.Profile}
          onChange={(e) => setNewData({ ...newData, Profile: e.target.value })}
          placeholder="Profile"
        />
        <input
          type="text"
          value={newData.Country}
          onChange={(e) => setNewData({ ...newData, Country: e.target.value })}
          placeholder="Country"
        />
        <button className="p-button p-button-success p-ml-2" onClick={addRow}>
          Add
        </button>
      </div>
      <DataTable
        value={formData}
        editMode="cell"
        dataKey="id"
        cellEditor={actionEditorTemplate}
        onRowEditInit={(e) => setEditingRows((prevState) => ({ ...prevState, [e.data.id]: true }))}
        onRowEditCancel={(e) => setEditingRows((prevState) => ({ ...prevState, [e.data.id]: false }))}
        rowEditorValidator={(rowData) => {
          return rowData.Name && rowData.mailId && rowData.Profile && rowData.Country;
        }}
      >
        {columns.map((column) => (
          <Column
            key={column.field}
            field={column.field}
            header={column.header}
            editor={(props) => column.editor && column.editor(props)}
          />
        ))}
      </DataTable>
      <div style={{ height: '300px', marginTop: '2rem' }}>
        <Chart type="pie" data={pieChartData} />
      </div>
    </div>
  );
};

export default DataTableComponent;
