import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
// import { format } from 'date-fns';
// import { time } from 'chartjs-adapter-date-fns';

// Chart.register(time);
Chart.register(...registerables);

const CombinedBarChart = ({ datasets }) => {
  const dates = datasets[0].data.map(entry => new Date(entry.date));

  const options = {
    indexAxis: 'y',
    plugins: {
      title: {
        display: true,
        text: 'Total Invocations',
        align: 'start',
        padding: {
          top: 10,
          left: 20,
        },
      },
      legend: {
        position: 'right',
      },
    },
    scales: {
      x: {
        stacked: true,
        beginAtZero: true,
        title: {
          display: true,
          text: 'Remediation',
        },
      },
      y: {
        stacked: true,
        ticks: {
          autoSkip: false,
        },
        type: 'category', // Use 'category' scale type for specific date labels
        labels: dates.map(date => format(date, 'MMM dd')), // Format and use the dates as labels
      },
    },
  };

  return (
    <div className="combined-bar-chart">
      <Bar data={{ labels: datasets[0].data.map(entry => entry.remediation), datasets }} options={options} />
    </div>
  );
};

export default CombinedBarChart;
