import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

const EditableTable = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [profile, setProfile] = useState('');
  const [country, setCountry] = useState('');
  const [data, setData] = useState([]);

  const addRow = () => {
    if (!name || !email || !profile || !country) {
      alert('Please fill in all fields.');
      return;
    }

    setData((prevData) => [
      ...prevData,
      { name, email, profile, country }
    ]);

    // Clear input fields after adding data to the table
    setName('');
    setEmail('');
    setProfile('');
    setCountry('');
  };

  const onRowEdit = (event) => {
    const rowIndex = event.index;
    const updatedData = [...data];
    updatedData[rowIndex] = event.data;
    setData(updatedData);
  };

  return (
    <div>
      <h2>Editable Table</h2>
      <form>
        <InputText id="name" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
        <InputText id="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <InputText id="profile" placeholder="Profile" value={profile} onChange={(e) => setProfile(e.target.value)} required />
        <InputText id="country" placeholder="Country" value={country} onChange={(e) => setCountry(e.target.value)} required />

        <Button type="button" label="Add" onClick={addRow} className="p-button-primary" />
      </form>

      <DataTable value={data} editMode="row" dataKey="email" onRowEdit={onRowEdit}>
        <Column field="name" header="Name" editor={(props) => <InputText type="text" value={props.rowData['name']} onChange={(e) => onRowEdit({ ...props, data: { ...props.rowData, name: e.target.value } })} />} />
        <Column field="email" header="Email" editor={(props) => <InputText type="text" value={props.rowData['email']} onChange={(e) => onRowEdit({ ...props, data: { ...props.rowData, email: e.target.value } })} />} />
        <Column field="profile" header="Profile" editor={(props) => <InputText type="text" value={props.rowData['profile']} onChange={(e) => onRowEdit({ ...props, data: { ...props.rowData, profile: e.target.value } })} />} />
        <Column field="country" header="Country" editor={(props) => <InputText type="text" value={props.rowData['country']} onChange={(e) => onRowEdit({ ...props, data: { ...props.rowData, country: e.target.value } })} />} />
        <Column rowEditor style={{ width: '70px' }} />
      </DataTable>
    </div>
  );
};

export default EditableTable;
