import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const CombinedBarChart = ({ datasets }) => {
    const dates = datasets[0].data.map(entry => entry.date);
  const chartData = {
    labels: datasets[0].data.map(entry => entry.date),
    datasets: datasets.map(dataset => ({
      label: dataset.label,
      backgroundColor: dataset.backgroundColor,
      data: dataset.data.map(entry => entry.remediation), // Use remediation values for x-axis
    })),
  };

  const options = {
    indexAxis: 'y',
    plugins: {
      legend: {
        position: 'right',
      },
    },
    scales: {
        y: {
            stacked: true,
            ticks: {
              autoSkip: false,
            },
            type: 'category', // Use 'category' scale type for specific date labels
            labels: dates, // Use the dates as labels
          },
      x: {
        stacked: true,
        beginAtZero: true,
      },
    },
  };

  return (
    <div className="combined-bar-chart">
      <Bar data={chartData} options={options} />
    </div>
  );
};

export default CombinedBarChart;
