import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);
// ... (previous code)

const CombinedBarChart = ({ data1, data2 }) => {
    const combinedData = data1.map(entry1 => {
      const matchingEntry = data2.find(entry2 => entry2.date === entry1.date);
      const totalInvocationsData1 = entry1.totalInvocations;
      const totalInvocationsData2 = matchingEntry ? matchingEntry.totalInvocations : 0;
      const totalInvocationsCombined = totalInvocationsData1 + totalInvocationsData2;
  
      return {
        date: entry1.date,
        totalInvocationsData1,
        totalInvocationsData2,
        totalInvocationsCombined,
      };
    });
  
    const chartData = {
      labels: combinedData.map(entry => entry.date),
      datasets: [
        {
          label: 'Total Invocations (Data 1)',
          backgroundColor: 'rgba(66, 165, 245, 0.6)', // Blue color for Data 1
          data: combinedData.map(entry => entry.totalInvocationsData1),
        },
        {
          label: 'Total Invocations (Data 2)',
          backgroundColor: 'rgba(255, 99, 132, 0.6)', // Red color for Data 2
          data: combinedData.map(entry => entry.totalInvocationsData2),
        },
        {
          label: 'Total Invocations Combined',
          backgroundColor: 'rgba(75, 192, 192, 0.6)', // Green color for combined data
          data: combinedData.map(entry => entry.totalInvocationsCombined),
        },
      ],
    };
  
    const options = {
      indexAxis: 'y', // Use 'y' for horizontal bar chart
      scales: {
        x: {
          beginAtZero: true,
        },
        y: {
          ticks: {
            autoSkip: false,
          },
        },
      },
    };
  
    return (
      <div className="combined-bar-chart">
        <Bar data={chartData} options={options} />
      </div>
    );
  };
  
  export default CombinedBarChart;
