const Navigation = ({ navItems }) => {
  const [activeSubMenu, setActiveSubMenu] = useState(null);

  const handleSubMenuClick = (index) => {
    setActiveSubMenu(activeSubMenu === index ? null : index);
  };

  return (
    <div
      style={{
        backgroundColor: '#000',
        color: '#fff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '1rem',
        position: 'relative', // Adjusted positioning
        zIndex: 1, // Ensuring the navigation stays on top
        transition: 'background-color 0.3s',
      }}
    >
      <div>
        <span style={{ fontSize: '2.5rem', fontWeight: 'bold', display: 'flex', alignItems: 'center' }}>
          <img src={logo} alt="logo" style={{ height: '100px', marginRight: '10px' }} />
          <span style={{ fontSize: '1.5rem', marginLeft: '1rem' }}>
            <ul style={{ listStyle: 'none', margin: 0, padding: 0, display: 'flex' }}>
              {navItems.map((item, index) => (
                <li
                  key={index}
                  onClick={() => handleSubMenuClick(index)}
                  style={{ position: 'relative' }}
                >
                  <NavItem title={item.title} submenu={item.submenu} url={item.url} />
                </li>
              ))}
            </ul>
          </span>
        </span>
      </div>
      {activeSubMenu !== null && (
        <div
          style={{
            position: 'absolute',
            top: '100%', // Positioned below the main navigation
            left: 0,
            zIndex: 0,
            width: '100%', // Extend the black component
            height: '100%', // Extend the black component
            backgroundColor: '#000', // Semi-transparent black
          }}
        >
          <ul style={{ listStyle: 'none', margin: 0, padding: 0, display: 'flex' }}>
            {navItems[activeSubMenu]?.submenu.map((item, index) => (
              <li
                key={index}
                style={{ marginRight: '1rem', position: 'relative' }}
              >
                <SubMenuItem title={item.title} url={item.url} submenu={item.submenu} />
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};
