import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js';

const MultiBarGraph = ({ dataTables }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      const ctx = chartRef.current.getContext('2d');
      const datasets = [];
      let labels = [];

      dataTables.forEach((table, index) => {
        const data = table.map(row => row.totalInvocations);
        labels = table.map(row => row.date);
        const color = getRandomColor();

        datasets.push({
          label: `Remediation ${index + 1}`,
          data,
          backgroundColor: color,
          borderColor: color,
          borderWidth: 1,
        });
      });

      new Chart(ctx, {
        type: 'bar',
        data: {
          labels,
          datasets,
        },
        options: {
          scales: {
            x: {
              stacked: true,
            },
            y: {
              stacked: true,
            },
          },
        },
      });
    }
  }, [dataTables]);

  return <canvas ref={chartRef} />;
};

function getRandomColor() {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

export default MultiBarGraph;
