import React, { useState } from 'react';
import DataTableComponent from './DataTableComponent';
import MenuBarComponent from './MenuBarComponent';
import PieChartComponent from './PieChartComponent';
import 'primeicons/primeicons.css'; // Import the primeicons CSS
import 'primereact/resources/themes/saga-blue/theme.css'; // Import PrimeReact theme
import 'primereact/resources/primereact.min.css'; // Import PrimeReact styles
import 'primeflex/primeflex.css'; // Import PrimeFlex styles

function App() {
  const [formData, setFormData] = useState([]);

  const addFormData = (data) => {
    setFormData((prevData) => [...prevData, data]);
  };

  return (
    <div>
      <MenuBarComponent addFormData={addFormData} />
      <DataTableComponent formData={formData} />
      <PieChartComponent formData={formData} />
    </div>
  );
}

export default App;
