import React, { useState, useEffect } from 'react';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Chart } from 'primereact/chart';
import logo from './verizon_logo.jpg';

const rowStyles = {
  '&.p-datatable-row': {
    backgroundColor: '#f8f8f8',
  },
};

const rowEvenStyles = {
  '&.p-datatable-row': {
    backgroundColor: '#ffffff',
  },
};

const chartContainerStyles = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  height: '100vh', // Set the height of the container to fill the viewport vertically
};

const chartStyles = {
  width: '500px', // Set the desired width of the pie chart
  height: '500px', // Set the desired height of the pie chart
};

const EditableTable = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [profile, setProfile] = useState([]);
  const [country, setCountry] = useState('');
  const [data, setData] = useState([]);
  const [editingRows, setEditingRows] = useState({});
  const [countryData, setCountryData] = useState({});
  const [displayChart, setDisplayChart] = useState(false);

  const addRow = () => {
    if (!name || !country || !email || !profile) {
      alert('Please fill in all fields.');
      return;
    }

    setData((prevData) => [
      ...prevData,
      { name, email, profile, country }
    ]);

    // Clear input fields after adding data to the table
    setName('');
    setEmail('');
    setProfile([]);
    setCountry('');
  };

  const debounce = (fn, delay) => {
    let timer;
    return function (...args) {
      clearTimeout(timer);
      timer = setTimeout(() => fn.apply(this, args), delay);
    };
  };

  const onRowEditInit = (event) => {
    const rowIndex = event.index;
    let editingRowsCopy = { ...editingRows };
    editingRowsCopy[rowIndex] = { ...data[rowIndex] };
    setEditingRows(editingRowsCopy);
  };

  const onRowEditSave = (event) => {
    const rowIndex = event.index;
    let updatedData = [...data];
    updatedData[rowIndex] = editingRows[rowIndex];
    setData(updatedData);
    setEditingRows({});
  };

  const onRowEditCancel = (event) => {
    setEditingRows({});
  };

  const onEditorValueChange = (props, value) => {
    const rowIndex = props.rowIndex;
    const field = props.field;
    setEditingRows((prevEditingRows) => {
      const updatedRow = { ...prevEditingRows[rowIndex], [field]: value };
      return { ...prevEditingRows, [rowIndex]: updatedRow };
    });
  };

  const prepareCountryData = () => {
    const countries = data.map((item) => item.country);
    const countryCount = {};
    countries.forEach((country) => {
      countryCount[country] = (countryCount[country] || 0) + 1;
    });
    setCountryData(countryCount);
  };

  useEffect(() => {
    prepareCountryData();
  }, [data]);

  const countryDataForChart = {
    labels: Object.keys(countryData),
    datasets: [
      {
        data: Object.values(countryData),
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#7C4DFF', '#00D0FF', '#FF5C93'],
        hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#7C4DFF', '#00D0FF', '#FF5C93']
      }
    ]
  };

  const headerStyles = {
    backgroundColor: '#000',
    color: '#fff',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center', // Add this line to vertically align items
    padding: '1rem',
  };

  const headerItemStyles = {
    padding: '0.5rem',
    cursor: 'pointer',
    display: 'flex', // Add this line to make the items inside flex container
    alignItems: 'center', // Add this line to vertically align items
  };

  const logoStyles = {
    height: '50px', // Set the desired height for the logo
    marginRight: '10px', // Add some spacing between the logo and text
  };

  return (
    <div>
      <div style={headerStyles}>
        <div style={headerItemStyles}>
          <span style={{ fontSize: '1.5rem', fontWeight: 'bold', display: 'flex', alignItems: 'center' }}>
            <img src={logo} alt="logo" style={logoStyles} />
            <span>
              <span style={{ fontSize: '1.5rem', marginLeft: '1rem', cursor: 'pointer' }} onClick={() => setDisplayChart(false)}>
                Home Page
              </span>
              <span style={{ fontSize: '1.2rem', marginLeft: '1rem', cursor: 'pointer' }} onClick={() => setDisplayChart(true)}>
                Records
              </span>
            </span>
          </span>
          {displayChart}
        </div>
      </div>

      <div style={{ margin: '1rem' }}>
        {!displayChart && (
          <React.Fragment>
            <form>
              <InputText id="name" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
              <InputText id="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
              <InputText id="profile" placeholder="Profile" value={profile} onChange={(e) => setProfile(e.target.value)} required />
              <InputText id="country" placeholder="Country" value={country} onChange={(e) => setCountry(e.target.value)} required />

              <Button type="button" label="Add" onClick={addRow} className="p-button-primary" />
            </form>

            <br/><br/>
            <DataTable
              value={data}
              editMode="row"
              dataKey="email"
              onRowEditInit={onRowEditInit}
              onRowEditSave={onRowEditSave}
              onRowEditCancel={onRowEditCancel}
              className="p-datatable-striped"
              rowClassName={(rowData, rowIndex) => (rowIndex % 2 === 0 ? 'row-even' : 'row-odd')}
              rowStyleClass="p-datatable-row"
            >
              <Column
                field="name"
                header="Name"
                editor={(props) =>
                  editingRows[props.rowIndex] ? (
                    <InputText
                      type="text"
                      value={editingRows[props.rowIndex]['name']}
                      onChange={(e) => onEditorValueChange(props, e.target.value)}
                    />
                  ) : (
                    props.rowData['name']
                  )
                }
              />
              <Column
                field="email"
                header="Email"
                editor={(props) =>
                  editingRows[props.rowIndex] ? (
                    <InputText
                      type="text"
                      value={editingRows[props.rowIndex]['email']}
                      onChange={(e) => onEditorValueChange(props, e.target.value)}
                    />
                  ) : (
                    props.rowData['email']
                  )
                }
              />
              <Column
                field="profile"
                header="Profile"
                editor={(props) =>
                  editingRows[props.rowIndex] ? (
                    <InputText
                      type="text"
                      value={editingRows[props.rowIndex]['profile']}
                      onChange={(e) => onEditorValueChange(props, e.target.value)}
                    />
                  ) : (
                    props.rowData['profile']
                  )
                }
              />
              <Column
                field="country"
                header="Country"
                editor={(props) =>
                  editingRows[props.rowIndex] ? (
                    <InputText
                      type="text"
                      value={editingRows[props.rowIndex]['country']}
                      onChange={(e) => onEditorValueChange(props, e.target.value)}
                    />
                  ) : (
                    props.rowData['country']
                  )
                }
              />
              <Column rowEditor style={{ width: '70px' }} />
            </DataTable>
          </React.Fragment>
        )}

        {displayChart && (
          <div className="chart-container" style={chartContainerStyles}>
            <div style={{ textAlign: 'center' }}>
              <h3>Country Statistics</h3>
              <Chart type="pie" data={countryDataForChart} style={chartStyles} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EditableTable;
