import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { saveAs } from 'file-saver';
import './CombinedBarChart.css';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const CombinedBarChart = ({ datasets }) => {
  const labels = datasets.length > 0 ? datasets[0].data.map(entry => entry.date) : [];

  const chartData = {
    labels,
    datasets: datasets.map((dataset, index) => ({
      label: `Data Table ${index + 1}`,
      backgroundColor: getBackgroundColor(index),
      data: dataset.data.map(entry => entry.totalInvocations),
    })),
  };

  const options = {
    indexAxis: 'y',
    scales: {
      x: {
        stacked: true,
      },
      y: {
        stacked: false,
      },
    },
    plugins: {
      legend: {
        display: true,
        position: 'top',
        labels: {
          usePointStyle: true,
        },
      },
    },
  };

  const [menuOpen, setMenuOpen] = useState(false);

  const handleDownload = async format => {
    if (format === 'CSV') {
      const csvContent = chartData.labels.join(',') + '\n' +
        datasets.map(dataset => dataset.data.map(entry => entry.totalInvocations).join(',')).join('\n');
      
      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8' });
      saveAs(blob, 'chart_data.csv');
    } else {
      const chart = document.querySelector('.chartjs-render-monitor');
      if (!chart) {
        return;
      }

      const canvas = chart.querySelector('canvas');
      if (!canvas) {
        return;
      }

      canvas.toBlob(blob => {
        saveAs(blob, `chart.${format.toLowerCase()}`);
      });
    }
  };
  
  return (
    <div className="combined-bar-chart">
      <div className="chart-container">
        <div className="chart">
          <Bar data={chartData} options={options} />
        </div>
      </div>
      <div className={`menu-container ${menuOpen ? 'open' : ''}`}>
        <div className="hamburger-icon" onClick={() => setMenuOpen(!menuOpen)}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </div>
        <div className={`download-menu ${menuOpen ? 'open' : ''}`}>
          <div className="menu-item" onClick={() => handleDownload('SVG')}>
            Download SVG
          </div>
          <div className="menu-item" onClick={() => handleDownload('PNG')}>
            Download PNG
          </div>
          <div className="menu-item" onClick={() => handleDownload('CSV')}>
            Download CSV
          </div>
        </div>
      </div>
    </div>
  );
};

const getBackgroundColor = index => {
  const colors = ['#FF5733', '#33FF57', '#3357FF']; // Example colors
  return colors[index % colors.length];
};

export default CombinedBarChart;
