import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

const EditableTable = () => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [email, setEmail] = useState('');
  const [data, setData] = useState([]); // Initialize data state as an empty array

  const addRow = () => {
    if (!name || !age || !email) {
      alert("Please fill in all fields.");
      return;
    }

    setData((prevData) => [
      ...prevData,
      { name, age, email }
    ]);

    // Clear input fields after adding data to the table
    setName('');
    setAge('');
    setEmail('');
  };

  const onRowEditInit = (event) => {
    // Clone the original data before editing to revert changes on cancel
    const clonedData = [...data];
    setData(clonedData);
  };

  const onRowEditSave = (event) => {
    // Save the edited data
    setData(event.data);
  };

  const onRowEditCancel = (event) => {
    // Revert changes on cancel
    setData(data);
  };

  return (
    <div>
      <h2>Editable Table</h2>
      <form>
        <label htmlFor="name">Name:</label>
        <InputText id="name" value={name} onChange={(e) => setName(e.target.value)} required />

        <label htmlFor="age">Age:</label>
        <InputText id="age" value={age} onChange={(e) => setAge(e.target.value)} required />

        <label htmlFor="email">Email:</label>
        <InputText id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

        <Button type="button" label="Add" onClick={addRow} className="p-button-primary" />
      </form>

      <DataTable
        value={data}
        editable
        editMode="row"
        dataKey="email"
        onRowEditInit={onRowEditInit}
        onRowEditSave={onRowEditSave}
        onRowEditCancel={onRowEditCancel}
      >
        <Column field="name" header="Name" editor={(props) => <InputText type="text" value={props.rowData['name']} onChange={(e) => props.onRowEdit({ ...props, data: { ...props.rowData, name: e.target.value } })} />} />
        <Column field="age" header="Age" editor={(props) => <InputText type="text" value={props.rowData['age']} onChange={(e) => props.onRowEdit({ ...props, data: { ...props.rowData, age: e.target.value } })} />} />
        <Column field="email" header="Email" editor={(props) => <InputText type="text" value={props.rowData['email']} onChange={(e) => props.onRowEdit({ ...props, data: { ...props.rowData, email: e.target.value } })} />} />
        <Column rowEditor style={{ width: '70px' }} />
      </DataTable>
    </div>
  );
};

export default EditableTable;
