import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

const EditableTable = () => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [email, setEmail] = useState('');
  const [data, setData] = useState([]);

  const addRow = () => {
    if (!name || !age || !email) {
      alert("Please fill in all fields.");
      return;
    }

    setData((prevData) => [
      ...prevData,
      { name, age, email }
    ]);

    // Clear input fields after adding data to the table
    setName('');
    setAge('');
    setEmail('');
  };

  const onRowEditInit = (event) => {
    // Clone the original data before editing to revert changes on cancel
    const clonedData = [...data];
    setData(clonedData);
  };

  const onRowEditSave = (event) => {
    // Save the edited data
    setData(event.data);
    // Remove the row from the editing state
    setEditingRows({});
  };

  const onRowEditCancel = (event) => {
    // Revert changes on cancel
    setData(data);
    // Remove the row from the editing state
    setEditingRows({});
  };

  const [editingRows, setEditingRows] = useState({});

  const onEditorValueChange = (props, value) => {
    // Update the editing state when an input field is changed during inline editing
    let updatedEditingRows = { ...editingRows };
    updatedEditingRows[props.rowIndex] = { ...props.rowData, [props.field]: value };
    setEditingRows(updatedEditingRows);
  };

  return (
    <div>
      <h2>Editable Table</h2>
      <form>
        <label htmlFor="name">Name:</label>
        <InputText id="name" value={name} onChange={(e) => setName(e.target.value)} required />

        <label htmlFor="age">Age:</label>
        <InputText id="age" value={age} onChange={(e) => setAge(e.target.value)} required />

        <label htmlFor="email">Email:</label>
        <InputText id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

        <Button type="button" label="Add" onClick={addRow} className="p-button-primary" />
      </form>

      <DataTable
        value={data}
        editMode="row"
        dataKey="email" // Use a unique key to identify rows during inline editing
        onRowEditInit={onRowEditInit}
        onRowEditSave={onRowEditSave}
        onRowEditCancel={onRowEditCancel}
      >
        <Column field="name" header="Name" editor={(props) => editingRows[props.rowIndex] ? <InputText type="text" value={editingRows[props.rowIndex]['name']} onChange={(e) => onEditorValueChange(props, e.target.value)} /> : props.rowData['name']} />
        <Column field="age" header="Age" editor={(props) => editingRows[props.rowIndex] ? <InputText type="text" value={editingRows[props.rowIndex]['age']} onChange={(e) => onEditorValueChange(props, e.target.value)} /> : props.rowData['age']} />
        <Column field="email" header="Email" editor={(props) => editingRows[props.rowIndex] ? <InputText type="text" value={editingRows[props.rowIndex]['email']} onChange={(e) => onEditorValueChange(props, e.target.value)} /> : props.rowData['email']} />
        <Column rowEditor style={{ width: '70px' }} />
      </DataTable>
    </div>
  );
};

export default EditableTable;
